  void ValidPropertyParsedToPropertyList_Success()
  {
    mitk::CustomTagParser tagParser(m_PathToModule);
    auto tsproperty = mitk::TemporoSpatialStringProperty::New();
    tsproperty->SetValue(0, 0, m_ValidCESTCustomTag);
    auto parsedPropertyList = tagParser.ParseDicomProperty(tsproperty);

    std::string sampling = "";
    std::string offset = "";
    std::string offsets = "";
    std::string measurements = "";
    std::string revision = "";
    std::string jsonRevision = "";
    bool hasRevision = parsedPropertyList->GetStringProperty("CEST.Revision", revision);
    parsedPropertyList->GetStringProperty("CEST.Offset", offset);
    parsedPropertyList->GetStringProperty(mitk::CEST_PROPERTY_NAME_OFFSETS().c_str(), offsets);
    parsedPropertyList->GetStringProperty("CEST.measurements", measurements);
    parsedPropertyList->GetStringProperty("CEST.SamplingType", sampling);
    parsedPropertyList->GetStringProperty("CEST.revision_json", jsonRevision);

    bool offsetsMatch =( offsets == "-300 -2 -1.86667 -1.73333 -1.6 -1.46667 -1.33333 -1.2 -1.06667 -0.933333 -0.8 -0.666667 -0.533333 -0.4 -0.266667 -0.133333 0 0.133333 0.266667 0.4 0.533333 0.666667 0.8 0.933333 1.06667 1.2 1.33333 1.46667 1.6 1.73333 1.86667 2");

    CPPUNIT_ASSERT_MESSAGE("Verify we found a revision.", hasRevision);
    CPPUNIT_ASSERT_MESSAGE("Verify the revision is the one we expect.", revision == "1416");
    CPPUNIT_ASSERT_MESSAGE("Verify the revision and the json revision match.", revision == jsonRevision);
    CPPUNIT_ASSERT_MESSAGE("Verify a couple of resulting properties match our expectation.", offset == "2" && sampling == "1" && measurements == "32");
    CPPUNIT_ASSERT_MESSAGE("Verify offsets are correctly parsed.", offsetsMatch);
  }

  void ValidPropertyMissingParametersParsedToEmptyPropertiesPropertyList_Success()
  {
    mitk::CustomTagParser tagParser(m_PathToModule);
    auto parsedPropertyList = tagParser.ParseDicomPropertyString(m_ValidCESTCustomTagAllParametersMissing);
    std::string revision = "";
    std::string jsonRevision = "";
    bool hasRevision = parsedPropertyList->GetStringProperty("CEST.Revision", revision);
    bool hasJsonRevision = parsedPropertyList->GetStringProperty("CEST.revision_json", jsonRevision);

    auto propertyMap = parsedPropertyList->GetMap();
    bool propertiesEmpty = true;
    for (auto const &prop : *propertyMap)
    {
      std::string key = prop.first;
      if (key != "CEST.Revision" && key != "CEST.revision_json")
      {
        propertiesEmpty = propertiesEmpty && prop.second->GetValueAsString() == "";
      }
    }

    CPPUNIT_ASSERT_MESSAGE("Verify we found a revision.", hasRevision);
    CPPUNIT_ASSERT_MESSAGE("Verify we found a json revision.", hasJsonRevision);
    CPPUNIT_ASSERT_MESSAGE("Property list properties are empty but for the revision information", propertiesEmpty);
  }

