  void ValidPropertyNoExactRevisionMatchUseExternal_Success()
  {
    std::string externalMappingString =
    "{\n"
      "  \"external mapping for test\" : \"revision_json\",\n"
      "  \"sWiPMemBlock.alFree[1]\" : \"AdvancedMode\"\n"
      "}";

    // we assume the test library will be in the same location as the MitkCEST library on windows
    // on linux the test driver should have a relative path of ../bin/
#ifdef _WIN32
    std::string dirname = m_PathToModule + "/CESTRevisionMapping";  
#else
    std::string dirname = m_PathToModule + "/../lib/CESTRevisionMapping";
#endif

    //bool dirWasThere = itksys::SystemTools::FileIsDirectory(dirname);

    std::string filename = dirname + "/118.json";

    itk::FileTools::CreateDirectory(dirname);

    std::ofstream externalFile(filename.c_str());

    if (externalFile.is_open())
    {
      externalFile << externalMappingString;
      externalFile.close();
    }

    mitk::CustomTagParser tagParser(m_PathToModule);
    auto parsedPropertyList = tagParser.ParseDicomPropertyString(m_ValidCESTCustomTagUnsupportedRevisionNoExactJSONUseExternal);
    std::string revision = "";
    std::string jsonRevision = "";
    bool hasRevision = parsedPropertyList->GetStringProperty("CEST.Revision", revision);
    bool hasJsonRevision = parsedPropertyList->GetStringProperty("CEST.revision_json", jsonRevision);
    bool usedExternal = (jsonRevision == "external mapping for test");

    CPPUNIT_ASSERT_MESSAGE("Verify we found a revision.", hasRevision);
    CPPUNIT_ASSERT_MESSAGE("Verify we found a json revision.", hasJsonRevision);
    CPPUNIT_ASSERT_MESSAGE("Verify we used external mapping.", usedExternal);

    bool wasError = std::remove(filename.c_str());

    if (wasError)
    {
      MITK_ERROR << "Could not delete test revision file";
    }
  }

